import './Profile.css';
import React from 'react';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function Profile(props) {
  const [isEdit, setIsEdit] = React.useState(false);
  const currentUser = React.useContext(CurrentUserContext);
  // const [name, setName] = React.useState('');
  // const [email, setEmail] = React.useState('');
  const [formValue, setFormValue] = React.useState({
    name: '',
    email: ''
  })

  React.useEffect(() => {
    setFormValue(currentUser.value)
    // setName(currentUser.name);
    // setEmail(currentUser.email);
  }, [currentUser]);

  function handleSubmit(e) {
    e.preventDefault();
    props.onUpdateUser({
      name,
      email,
    });
    setIsEdit(false);
  }

  function handleChangeUserName(e) {
    setName(e.target.value);
  }

  function handleChangeUserEmail(e) {
    setEmail(e.target.value);
  }

  return (
    <>
      <section className='profile'>
        <h1 className='profile__title'>{`Привет, ${currentUser.name}`}</h1>
        <form className='profile__form' onSubmit={handleSubmit}>
          <label className='profile__lable' htmlFor='profile-name'>
            Имя
            <input
              onChange={handleChangeUserName}
              value={name}
              className='profile__input'
              type='text'
              id='profile-name'
              name='profile-name'
              required
              minLength='2'
              maxLength='30'
              placeholder={currentUser.name}
              disabled={!isEdit}
            />
          </label>
          <hr className='profile__devider' />
          <label className='profile__lable' htmlFor='profile-email'>
            E-mail
            <input
              onChange={handleChangeUserEmail}
              value={email}
              className='profile__input'
              type='email'
              id='profile-email'
              name='profile-email'
              required
              minLength='2'
              maxLength='30'
              placeholder={currentUser.email}
              disabled={!isEdit}
            />
          </label>
          <div className='profile__save-container'>
            <p className='profile__error'>
              {/* При обновлении профиля произошла ошибка. */}
            </p>
            {isEdit ? (
              <button
                // disabled={
                //   (name === currentUser.name ? true : false) ||
                //   (email === currentUser.email ? true : false)
                // }
                className='profile__button'
              >
                Сохранить
              </button>
            ) : (
              <>
                <button
                  className='profile__edit'
                  onClick={() => setIsEdit(true)}
                >
                  Редактировать
                </button>
                <button className='profile__signout' onClick={props.onSignOut}>
                  Выйти из аккаунта
                </button>
              </>
            )}
          </div>
        </form>
      </section>
    </>
  );
}

export default Profile;
