import './App.css';
import React from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';
import Header from '../Header/Header';
import Register from '../Register/Register';
import Login from '../Login/Login';
import Profile from '../Profile/Profile';
import Main from '../Main/Main';
import Movies from '../Movies/Movies';
import SavedMovies from '../SavedMovies/SavedMovies';
import NotFound from '../NotFound/NotFound';
import { mainApi } from '../../utils/MainApi';
import { moviesApi } from '../../utils/MoviesApi';

function App() {
  const [currentUser, setCurrentUser] = React.useState({});
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [movies, setMovies] = React.useState(() => {
    const saved = localStorage.getItem('Movies');
    const initialValue = JSON.parse(saved);
    return initialValue || '';
  });
  const [savedMovies, setSavedMovies] = React.useState([]);
  const [foundMovies, setFoundMovies] = React.useState(() => {
    const saved = localStorage.getItem('storageMoviesCards');
    const initialValue = JSON.parse(saved);
    return initialValue || '';
  });
  const [searchInputValue, setSearchInputValue] = React.useState(() => {
    const saved = localStorage.getItem('storageMoviesText');
    const initialValue = JSON.parse(saved);
    return initialValue || '';
  });

    const [isSaved, setIsSaved] = React.useState(() => {
    const saved = localStorage.getItem('isMovieSaved');
    const initialValue = JSON.parse(saved);
    return initialValue || false;
  });
  // const [isSaved, setIsSaved] = React.useState(false)

    React.useEffect(() => {
    localStorage.setItem('isMovieSaved', JSON.stringify(isSaved));
  });

  const location = useLocation();
  const isHeaderLocation = ['/', '/profile', '/movies', '/saved-movies'];

  const navigate = useNavigate();

  React.useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      mainApi
        .checkToken(token)
        .then((res) => {
          if (res) {
            setLoggedIn(true);
          }
        })
        .catch((err) => {
          localStorage.removeItem('token');
          console.log(`Ошибка: ${err}`);
        });

      mainApi
        .getUserInfo()
        .then((res) => {
          setCurrentUser(res);
        })
        .catch((err) => {
          console.error(err);
        });
    }
  }, [loggedIn]);

  React.useEffect(() => {
    moviesApi
      .getMovieCards()
      .then((res) => {
        setMovies(res);
      })
      .catch((err) => {
        console.error(err);
      });
  }, [loggedIn]);

  React.useEffect(() => {
    mainApi
      .getMovie()
      .then((res) => {
        setSavedMovies(res.reverse());
      })
      .catch((err) => {
        console.error(err);
      });
  }, [loggedIn]);

  const handleRegistrate = (name, password, email) => {
    mainApi
      .register(name, password, email)
      .then((res) => {
        localStorage.setItem('token', res.token);
      })
      .catch((err) => {
        console.log(`Ошибка: ${err}`);
      });
  };

  const handleLogin = (data) => {
    mainApi
      .authorize(data)
      .then((res) => {
        localStorage.setItem('token', res.token);
        setLoggedIn(true);
        navigate('/movies');
      })
      .catch((err) => {
        console.log(`Ошибка: ${err}`);
      });
  };

  function handleLogOut() {
    localStorage.removeItem('token');
    setLoggedIn(false);
    navigate('/signin');
  }

  const handleUpdateUser = ({ name, email }) => {
    mainApi
      .addUserInfo({ name, email })
      .then((res) => {
        setCurrentUser(res);
      })
      .catch((err) => {
        console.log(`Ошибка: ${err}`);
      });
  };

  React.useEffect(() => {
    localStorage.setItem('storageMoviesText', JSON.stringify(searchInputValue));
    localStorage.setItem('storageMoviesCards', JSON.stringify(foundMovies));
    localStorage.setItem('Movies', JSON.stringify(movies));
  });

  function searchMovies() {
    const searchFilter = movies.filter((movie) => {
      return movie.nameRU
        .toLowerCase()
        .includes(searchInputValue.toLowerCase());
    });
    setFoundMovies(searchFilter);
  }

  function onSearchChange(e) {
    setSearchInputValue(e.target.value);
  }

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    searchMovies();
  };

  function saveMovie(movie) {
    mainApi
      .saveMovie({
        country: movie.country,
        director: movie.director,
        duration: movie.duration,
        year: movie.year,
        description: movie.description,
        image: `https://api.nomoreparties.co${movie.image.url}`,
        trailerLink: movie.trailerLink,
        thumbnail: `https://api.nomoreparties.co${movie.image.formats.thumbnail.url}`,
        movieId: movie.id,
        nameRU: movie.nameRU,
        nameEN: movie.nameEN,
      })
      .then((newSavedMovie) => {
        setSavedMovies([newSavedMovie, ...savedMovies]);
        setIsSaved(true)
      });
  }

  function handleMovieDelete(card) {
    api
      .deleteCards(card._id)
      .then(() => {
        setCards(cards.filter((c) => c._id !== card._id));
      })
      .catch((err) => {
        console.error(err);
      });
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='body'>
        <div className='page'>
          {isHeaderLocation.includes(location.pathname) ? (
            <Header loggedIn={loggedIn} />
          ) : (
            ''
          )}
          <Routes>
            <Route
              path='/signup'
              element={<Register handleRegistrate={handleRegistrate} />}
            />
            <Route
              path='/signin'
              element={<Login handleLogin={handleLogin} />}
            />
            <Route path='/' element={<Main />} />
            <Route
              path='/profile'
              element={
                <Profile
                  onSignOut={handleLogOut}
                  onUpdateUser={handleUpdateUser}
                />
              }
            />
            <Route
              path='/movies'
              element={
                <Movies
                  movies={foundMovies}
                  handleSearchSubmit={handleSearchSubmit}
                  searchInputValue={searchInputValue}
                  onSearchChange={onSearchChange}
                  onMovieCardSave={saveMovie}
                  isSaved={isSaved}
                  onMovieCardDelete={handleMovieDelete}
                />
              }
            />
            <Route
              path='/saved-movies'
              element={
                <SavedMovies movies={savedMovies} savedMovies={savedMovies} />
              }
            />
            <Route path='*' element={<NotFound />} />
          </Routes>
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
