import './App.css';
import React from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';
import Header from '../Header/Header';
import Register from '../Register/Register';
import Login from '../Login/Login';
import Profile from '../Profile/Profile';
import Main from '../Main/Main';
import Movies from '../Movies/Movies';
import SavedMovies from '../SavedMovies/SavedMovies';
import NotFound from '../NotFound/NotFound';
import { mainApi } from '../../utils/MainApi';
import ProtectedRoute from '../ProtectedRoute/ProtectedRoute';

function App() {
  const [isEdit] = React.useState(true);
  const [currentUser, setCurrentUser] = React.useState({});
  const [loggedIn, setLoggedIn] = React.useState(false);

  const headerLocation = useLocation();
  const isHeaderLocation = ['/', '/profile', '/movies', '/saved-movies'];

  const navigate = useNavigate();

  // React.useEffect(() => {
  //   const token = localStorage.getItem('token');

  //   if (token) {
  //     mainApi
  //       .checkToken(token)
  //       .then((res) => {
  //         if (res) {
  //           setLoggedIn(true);
  //           navigate('/');
  //         }
  //       })
  //       .catch((err) => {
  //         localStorage.removeItem('token');
  //         console.log(`Ошибка: ${err}`);
  //       });

  //     // api
  //     //   .getCards()
  //     //   .then((res) => {
  //     //     setCards(res.reverse());
  //     //   })
  //     //   .catch((err) => {
  //     //     console.error(err);
  //     //   });
  //     // api
  //     //   .getUserInfo()
  //     //   .then((res) => {
  //     //     setCurrentUser(res);
  //     //   })
  //     //   .catch((err) => {
  //     //     console.error(err);
  //     //   });
  //   }
  // }, [navigate, loggedIn]);

  const handleRegistrate = (name, password, email) => {
    mainApi
      .register(name, password, email)
      .then((res) => {
        // localStorage.setItem('token', res.token);
        navigate('/signin');
      })
      .catch((err) => {
        console.log(`Ошибка: ${err}`);
      });
  };

  const handleLogin = (data) => {
    mainApi
      .authorize(data)
      // .then((res) => {
      //   localStorage.setItem('token', res.token);
      //   setLoggedIn(true);
      //   navigate('/');
      // })
      // .catch((err) => {
      //   console.log(`Ошибка: ${err}`);
      // });
  };

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='body'>
        <div className='page'>
          {/* {headerLocation === isHeaderLocation ? (
            <Header loggedIn={loggedIn} />
          ) : (
            ''
          )} */}
          <Header loggedIn={loggedIn} />
          <Routes>
            <Route
              path='/'
              element={<ProtectedRoute loggedIn={loggedIn} element={Main} />}
            />
            <Route
              path='/signup'
              element={<Register handleRegistrate={handleRegistrate} />}
            />
            <Route
              path='/signin'
              element={<Login handleLogin={handleLogin} />}
            />
            {/* <Route path='/' element={<Main />} /> */}
            <Route path='profile' element={<Profile isEdit={isEdit} />} />
            <Route path='/movies' element={<Movies />} />
            <Route path='/saved-movies' element={<SavedMovies />} />
            <Route path='*' element={<NotFound />} />
          </Routes>
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
