import './MoviesCard.css';
import React from 'react';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function MoviesCard(props) {
  const currentUser = React.useContext(CurrentUserContext);

  return (
    <section className='movies-card'>
      <button
        className={`movies-card__button movies-card__button_type_${props.classType}`}
      >
        {props.buttonText}
      </button>
      <img
        className='movies-card__thumbnail'
        src={`props.movie.image.${'https://api.nomoreparties.co/'}.url`}
        alt={props.movie.nameRU}
      />
      <div className='movies-card__info'>
        <h2 className='movies-card__title'>{props.movie.nameRU}</h2>
        <div className='movies-card__duration-background'>
          <p className='movies-card__duration'>{props.movie.duration}</p>
        </div>
      </div>
    </section>
  );
}

export default MoviesCard;
