import './MoviesCard.css';
import React from 'react';

function MoviesCard(props) {
  const isHover = props.isHover;
  return (
    <section className='movies-card'>
      {isHover &&
        (props.isSaved ? (
          <button className='movies-card__button movies-card__already-saved'></button>
        ) : (
          <button className='movies-card__button movies-card__save'>
            Сохранить
          </button>
        ))}
      {/* {props.isSaved ? (
        <button className='movies-card__button movies-card__saved'></button>
      ) : (
        <button className='movies-card__button movies-card__save'>
          Сохранить
        </button>
      )} */}
      <img
        className='movies-card__thumbnail'
        src={props.thumbnail}
        alt='Заставка'
      />
      <div className='movies-card__info'>
        <h2 className='movies-card__title'>{props.title}</h2>
        <div className='movies-card__duration-background'>
          <p className='movies-card__duration'>{props.duration}</p>
        </div>
      </div>
    </section>
  );
}

export default MoviesCard;
