import './Register.css';
import { Link } from 'react-router-dom';
import React from 'react';

function Register(props) {
  const [name, setName] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');

  function handleChangeName(e) {
    setName(e.target.value);
  }

  function handleChangeEmail(e) {
    setEmail(e.target.value);
  }

  function handleChangePassword(e) {
    setPassword(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    props.handleRegistrate(name, password, email);
  }

  return (
    <section className='register'>
      <button className='register__logo'></button>
      <h1 className='register__title'>Добро пожаловать!</h1>
      <form
        onSubmit={handleSubmit}
        className='register__form'
      >
        <label className='register__lable' htmlFor='register-name'>
          Имя
        </label>
        <input
          value={name}
          onChange={handleChangeName}
          className='register__input'
          type='text'
          id='register-name'
          name='register-name'
          required
          minLength='2'
          maxLength='30'
          formNoValidate
        />
        <div className='register__error-container'>
          <span className='register__error name-error'></span>
        </div>
        <label className='register__lable' htmlFor='register-email'>
          E-mail
        </label>
        <input
          value={email}
          onChange={handleChangeEmail}
          className='register__input'
          type='email'
          id='register-email'
          name='register-email'
          required
          minLength='2'
          maxLength='30'
          formNoValidate
        />
        <div className='register__error-container'>
          <span className='register__error email-error'></span>
        </div>
        <label className='register__lable' htmlFor='register-password'>
          Пароль
        </label>
        <input
          value={password}
          onChange={handleChangePassword}
          className='register__input'
          type='password'
          id='register-password'
          name='register-password'
          required
          minLength='2'
          maxLength='30'
          formNoValidate
        />
        <div className='register__error-container'>
          <span className='register__error password-error'></span>
        </div>
        <div className='register__save-container'>
        <span className='register__save-error'>
          {props.errorMessage}
        </span>
        <button className='register__button' type='submit'>Зарегистрироваться</button>
      </div>
      </form>
      <div className='register__link-container'>
        <p className='register__link-text'>Уже зарегистрированы?</p>
        <Link to='/signin' className='register__link'>
          Войти
        </Link>
      </div>
    </section>
  );
}

export default Register;
