import './App.css';
import React from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';
import Header from '../Header/Header';
import Register from '../Register/Register';
import Login from '../Login/Login';
import Profile from '../Profile/Profile';
import Main from '../Main/Main';
import Movies from '../Movies/Movies';
import SavedMovies from '../SavedMovies/SavedMovies';
import NotFound from '../NotFound/NotFound';
import { mainApi } from '../../utils/MainApi';
// import ProtectedRoute from '../ProtectedRoute/ProtectedRoute';

function App() {
  const [currentUser, setCurrentUser] = React.useState({});
  const [loggedIn, setLoggedIn] = React.useState(false);
  // const [isCheckToken, setIsCheckToken] = React.useState(false);
  const [movies, setMovies] = React.useState([]);

  const location = useLocation();
  const isHeaderLocation = ['/', '/profile', '/movies', '/saved-movies'];

  const navigate = useNavigate();

  React.useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      mainApi
        .checkToken(token)
        .then((res) => {
          if (res) {
            setLoggedIn(true);
            // setIsCheckToken(true);
            // navigate('/');
          }
        })
        .catch((err) => {
          // setIsCheckToken(true);
          localStorage.removeItem('token');
          console.log(`Ошибка: ${err}`);
        });

        mainApi
        .getUserInfo()
        .then((res) => {
          setCurrentUser(res);
        })
        .catch((err) => {
          console.error(err);
        });
    }
  }, [navigate, loggedIn]);

  const handleRegistrate = (name, password, email) => {
    mainApi
      .register(name, password, email)
      .then((res) => {
        localStorage.setItem('token', res.token);
        // navigate('/signin');
      })
      .catch((err) => {
        console.log(`Ошибка: ${err}`);
      });
  };

  const handleLogin = (data) => {
    mainApi
      .authorize(data)
      .then((res) => {
        localStorage.setItem('token', res.token);
        setLoggedIn(true);
        navigate('/');
      })
      .catch((err) => {
        console.log(`Ошибка: ${err}`);
      });
  };

  function handleLogOut() {
    localStorage.removeItem('token');
    setLoggedIn(false);
    navigate('/signin');
  }

  const handleUpdateUser = ({ name, about }) => {
    mainApi
      .addUserInfo({ name, about })
      .then((res) => {
        setCurrentUser(res);
      })
      .catch((err) => {
        console.log(`Ошибка: ${err}`);
      });
  };

  return (
    <CurrentUserContext.Provider value={currentUser}>
      {/* {isCheckToken ? ( */}
      <div className='body'>
        <div className='page'>
          {isHeaderLocation.includes(location.pathname) ? (
            <Header loggedIn={loggedIn} />
          ) : (
            ''
          )}
          <Routes>
            {/* <Route
                path='/'
                element={<ProtectedRoute loggedIn={loggedIn} element={Main} />}
              /> */}
            <Route
              path='/signup'
              element={<Register handleRegistrate={handleRegistrate} />}
            />
            <Route
              path='/signin'
              element={<Login handleLogin={handleLogin} />}
            />
            <Route path='/' element={<Main />} />
            <Route
              path='/profile'
              element={
                <Profile
                  onSignOut={handleLogOut}
                  // isEdit={isEdit}
                  onUpdateUser={handleUpdateUser}
                />
              }
            />
            <Route path='/movies' element={<Movies />} />
            <Route path='/saved-movies' element={<SavedMovies />} />
            <Route path='*' element={<NotFound />} />
          </Routes>
        </div>
      </div>
      {/* ) : (
        ''
      )} */}
    </CurrentUserContext.Provider>
  );
}

export default App;
