import './Profile.css';
import React from 'react';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';
import { useFormWithValidation } from '../../hooks/useFormWithValidation';

function Profile(props) {
  const [isEdit, setIsEdit] = React.useState(false);
  const currentUser = React.useContext(CurrentUserContext);
  const { values, setValues, handleChange, errors, isValid, resetForm } =
    useFormWithValidation({});
  React.useEffect(() => {
    setValues({ name: currentUser.name, email: currentUser.email });
  }, [currentUser, setValues]);

  function handleSubmit(e) {
    e.preventDefault();
    props.onUpdateUser({
      name: values.name,
      email: values.email,
    });
    setIsEdit(false);
  }

  // function handleChangeUserName(e) {
  //   setName(e.target.value);
  // }

  // function handleChangeUserEmail(e) {
  //   setEmail(e.target.value);
  // }

  return (
    <>
      <section className='profile'>
        <h1 className='profile__title'>{`Привет, ${currentUser.name}`}</h1>
        <form className='profile__form' onSubmit={handleSubmit}>
          <label className='profile__lable' htmlFor='profile-name'>
            Имя
            <input
              onChange={handleChange}
              value={values.name}
              className='profile__input'
              type='text'
              id='profile-name'
              name='name'
              required
              minLength='2'
              maxLength='30'
              placeholder={currentUser.name}
              disabled={!isEdit}
            />
          </label>
          <hr className='profile__devider' />
          <label className='profile__lable' htmlFor='profile-email'>
            E-mail
            <input
              onChange={handleChange}
              value={values.email}
              className='profile__input'
              type='email'
              id='profile-email'
              name='email'
              required
              minLength='2'
              maxLength='30'
              placeholder={currentUser.email}
              disabled={!isEdit}
            />
          </label>
          <div className='profile__save-container'>
            <p className='profile__error'>
              {!isValid ? errors.name : ''}
            </p> 
            {isEdit ? (
              <button
                // disabled={
                //   name === currentUser.name ||
                //   email === currentUser.email ||
                //   (name === currentUser.name && email === currentUser.email)
                //   // (name === currentUser.name ? true : false) ||
                //   // (email === currentUser.email ? true : false)
                // }
                className='profile__button'
              >
                Сохранить
              </button>
            ) : (
              <>
                <button
                  className='profile__edit'
                  onClick={() => setIsEdit(true)}
                >
                  Редактировать
                </button>
                <button className='profile__signout' onClick={props.onSignOut}>
                  Выйти из аккаунта
                </button>
              </>
            )}
          </div>
        </form>
      </section>
    </>
  );
}

export default Profile;
