import './MoviesCard.css';
import React from 'react';
import { useLocation } from 'react-router-dom';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function MoviesCard(props) {
  const [isSaved, setIsSaved] = React.useState(() => {
    const saved = localStorage.getItem('isMovieSaved');
    const initialValue = JSON.parse(saved);
    return initialValue || false;
  });
  const cardLocation = useLocation();
  const movieLocation = '/movies';
  const currentUser = React.useContext(CurrentUserContext);

  React.useEffect(() => {
    localStorage.setItem('isMovieSaved', JSON.stringify(isSaved));
  });

  // const isSaved = props.savedMovies.find(
  //   (movie) => movie.id === currentUser.id
  // );
  // const saveButtonClassName = `movies-card__button ${
  //   isSaved && 'movies-card__button_type_already-saved'
  // }`;

  function handleMovieCardSave() {
    if (!isSaved) {
      props.onMovieCardSave(props.movie);
      setIsSaved(true);
    } else {
      console.log('hfvk');
    }
  }

  // function handleMovieCardDelete() {
  //   props.onMovieCardDelete(props.movie);
  // }

  return (
    <section className='movies-card'>
      {cardLocation.pathname === movieLocation ? (
        <button
          className={`movies-card__button ${
            isSaved
              ? 'movies-card__button_type_already-saved'
              : 'movies-card__button_type_save'
          }`}
          onClick={() => !isSaved ? handleMovieCardSave && setIsSaved(false) : ''}
        >
          {!isSaved ? 'Сохранить' : ''}
        </button>
      ) : (
        <button className='movies-card__button movies-card__button_type_delete'></button>
      )}

      {/* {isSaved ? (
        <button className={saveButtonClassName}></button>
      ) : (
        <button
          className='movies-card__button movies-card__button_type_save'
          onClick={handleMovieCardSave}
        >
          Сохранить
        </button>
      )} */}
      <img
        className='movies-card__thumbnail'
        src={
          cardLocation.pathname === movieLocation
            ? `https://api.nomoreparties.co/${props.movie.image.url}`
            : props.movie.image
        }
        alt={props.movie.nameRU}
      />
      <div className='movies-card__info'>
        <h2 className='movies-card__title'>{props.movie.nameRU}</h2>
        <div className='movies-card__duration-background'>
          <p className='movies-card__duration'>{props.movie.duration}</p>
        </div>
      </div>
    </section>
  );
}

export default MoviesCard;
